"use client";

/**
 * Knowledge Base - Professor Gemini Generated Guides
 *
 * Documents generated by Professor Gemini (gemini-responses folder)
 * Features a sidebar TOC for quick navigation between documents
 */

import { useState } from "react";
import Link from "next/link";
import { useSearchParams, useRouter } from "next/navigation";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import { AuthGate } from "@/components/AuthGate";
import { knowledgeBaseDocs, getKnowledgeBaseDoc } from "@/data/knowledge-base";
import { useEffect, useRef } from "react";
import mermaid from "mermaid";

// Initialize mermaid with dark mode support
mermaid.initialize({
  startOnLoad: false,
  theme: "neutral",
  securityLevel: "loose",
  fontFamily: "inherit",
});

function formatDate(dateString: string): string {
  const date = new Date(dateString);
  return date.toLocaleDateString("en-US", {
    year: "numeric",
    month: "short",
    day: "numeric",
  });
}

// Mermaid diagram component
function MermaidDiagram({ chart }: { chart: string }) {
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (containerRef.current) {
      const id = `mermaid-${Math.random().toString(36).substring(2, 9)}`;
      containerRef.current.innerHTML = "";

      mermaid
        .render(id, chart)
        .then(({ svg }) => {
          if (containerRef.current) {
            containerRef.current.innerHTML = svg;
          }
        })
        .catch((error) => {
          console.error("Mermaid render error:", error);
          if (containerRef.current) {
            containerRef.current.innerHTML = `<pre class="text-red-500">Diagram error: ${error.message}</pre>`;
          }
        });
    }
  }, [chart]);

  return (
    <div
      ref={containerRef}
      className="my-6 flex justify-center overflow-x-auto"
    />
  );
}

// Custom code block renderer
function CodeBlock({
  className,
  children,
}: {
  className?: string;
  children?: React.ReactNode;
}) {
  const match = /language-(\w+)/.exec(className || "");
  const language = match ? match[1] : "";
  const code = String(children).replace(/\n$/, "");

  if (language === "mermaid") {
    return <MermaidDiagram chart={code} />;
  }

  return (
    <code
      className={`${className || ""} font-mono text-sm`}
      style={{ fontFamily: "ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, monospace" }}
    >
      {children}
    </code>
  );
}

function KnowledgeBaseContent() {
  const searchParams = useSearchParams();
  const router = useRouter();
  const selectedSlug = searchParams.get("doc") || (knowledgeBaseDocs[0]?.slug ?? null);
  const selectedDoc = selectedSlug ? getKnowledgeBaseDoc(selectedSlug) : null;
  const [sidebarOpen, setSidebarOpen] = useState(true);

  const handleDocSelect = (slug: string) => {
    router.push(`/nebula/knowledge-base?doc=${slug}`);
  };

  return (
    <div className="min-h-screen flex">
      {/* Sidebar */}
      <aside
        className={`${
          sidebarOpen ? "w-72" : "w-0"
        } flex-shrink-0 border-r border-border bg-muted/30 transition-all duration-300 overflow-hidden`}
      >
        <div className="w-72 h-full flex flex-col">
          {/* Sidebar Header */}
          <div className="p-4 border-b border-border bg-gradient-to-r from-primary/5 to-transparent">
            <div className="flex items-center gap-3">
              <div className="flex h-10 w-10 items-center justify-center rounded-lg bg-primary/10">
                <svg
                  className="h-5 w-5 text-primary"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.747 0 3.332.477 4.5 1.253v13C19.832 18.477 18.247 18 16.5 18c-1.746 0-3.332.477-4.5 1.253"
                  />
                </svg>
              </div>
              <div>
                <h1 className="text-lg font-semibold text-foreground">Knowledge Base</h1>
                <p className="text-xs text-muted-foreground">Professor Gemini Guides</p>
              </div>
            </div>
            <div className="mt-3 text-xs text-muted-foreground">
              {knowledgeBaseDocs.length} documents
            </div>
          </div>

          {/* Document List */}
          <nav className="flex-1 overflow-y-auto p-2">
            <div className="space-y-1">
              {knowledgeBaseDocs.map((doc) => (
                <button
                  key={doc.slug}
                  onClick={() => handleDocSelect(doc.slug)}
                  className={`w-full text-left px-3 py-2.5 rounded-lg transition-colors ${
                    selectedSlug === doc.slug
                      ? "bg-primary/10 text-primary border border-primary/30"
                      : "hover:bg-muted text-foreground"
                  }`}
                >
                  <div className="font-medium text-sm truncate">{doc.title}</div>
                  <div className="text-xs text-muted-foreground mt-0.5">
                    {formatDate(doc.date)}
                  </div>
                </button>
              ))}
            </div>

            {knowledgeBaseDocs.length === 0 && (
              <div className="text-center py-8 px-4">
                <p className="text-sm text-muted-foreground">No documents yet.</p>
                <p className="text-xs text-muted-foreground mt-2">
                  Generate guides with Professor Gemini.
                </p>
              </div>
            )}
          </nav>

          {/* Back Link */}
          <div className="p-3 border-t border-border">
            <Link
              href="/nebula"
              className="flex items-center gap-2 text-sm text-muted-foreground hover:text-primary transition-colors"
            >
              <svg
                className="h-4 w-4"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M15 19l-7-7 7-7"
                />
              </svg>
              Back to Nebula
            </Link>
          </div>
        </div>
      </aside>

      {/* Toggle Button */}
      <button
        onClick={() => setSidebarOpen(!sidebarOpen)}
        className="absolute left-0 top-1/2 -translate-y-1/2 z-10 bg-muted border border-border rounded-r-lg p-1.5 hover:bg-muted/80 transition-colors"
        style={{ left: sidebarOpen ? "288px" : "0" }}
      >
        <svg
          className={`h-4 w-4 text-muted-foreground transition-transform ${
            sidebarOpen ? "" : "rotate-180"
          }`}
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M15 19l-7-7 7-7"
          />
        </svg>
      </button>

      {/* Main Content */}
      <main className="flex-1 overflow-auto">
        {selectedDoc ? (
          <div className="max-w-4xl mx-auto px-6 py-8">
            {/* Document Header */}
            <header className="mb-8 pb-6 border-b border-border">
              <h1 className="text-2xl font-bold text-foreground sm:text-3xl">
                {selectedDoc.title}
              </h1>
              <div className="mt-3 flex items-center gap-4 text-sm text-muted-foreground">
                <span>{formatDate(selectedDoc.date)}</span>
                <span className="text-border">|</span>
                <span className="font-mono text-xs bg-muted px-2 py-1 rounded">
                  {selectedDoc.sourceFile}
                </span>
              </div>
            </header>

            {/* Document Content */}
            <article className="prose prose-neutral dark:prose-invert max-w-none prose-headings:scroll-mt-20 prose-a:text-primary prose-code:before:content-none prose-code:after:content-none prose-code:bg-muted prose-code:px-1.5 prose-code:py-0.5 prose-code:rounded prose-pre:bg-muted prose-pre:border prose-pre:border-border">
              <ReactMarkdown
                remarkPlugins={[remarkGfm]}
                components={{
                  code: CodeBlock,
                }}
              >
                {selectedDoc.content}
              </ReactMarkdown>
            </article>
          </div>
        ) : (
          <div className="flex items-center justify-center h-full">
            <div className="text-center">
              <div className="text-5xl mb-4">ðŸ“š</div>
              <h2 className="text-xl font-semibold text-foreground mb-2">
                No Document Selected
              </h2>
              <p className="text-muted-foreground">
                Select a document from the sidebar to view its contents.
              </p>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default function KnowledgeBasePage() {
  return (
    <AuthGate
      storageKey="cyrus_nebula_auth"
      title="Knowledge Base"
      subtitle="Professor Gemini Guides"
    >
      <KnowledgeBaseContent />
    </AuthGate>
  );
}
