export function getScratchPadDoc(slug: string): ScratchPadDoc | undefined {
  return scratchPadDocs.find(doc => doc.slug === slug);
}

export function getAllScratchPadSlugs(): string[] {
  return scratchPadDocs.map(doc => doc.slug);
}
